DROP TABLE IF EXISTS `messy_dates`;
CREATE TABLE `messy_dates` (
    `ID` INT(11) NOT NULL,
    `messy_date` VARCHAR(45) DEFAULT NULL,
    PRIMARY KEY (`ID`)
)  ENGINE=INNODB DEFAULT CHARSET=LATIN1;


CREATE VIEW `fine_dates_view` AS
    SELECT 
        *,
        IF(SUBSTR(messy_date, 3, 1) = '/',
            CONCAT(SUBSTR(messy_date, 7, 4),
                    '.',
                    SUBSTR(messy_date, 1, 2),
                    '.',
                    SUBSTR(messy_date, 4, 2)),
            CONCAT(SUBSTR(messy_date, 1, 4),
                    '.',
                    SUBSTR(messy_date, 6, 2),
                    '.',
                    SUBSTR(messy_date, 9, 2))) fine_date,
        DATEDIFF(NOW(),
                IF(SUBSTR(messy_date, 3, 1) = '/',
                    CONCAT(SUBSTR(messy_date, 7, 4),
                            '.',
                            SUBSTR(messy_date, 1, 2),
                            '.',
                            SUBSTR(messy_date, 4, 2)),
                    CONCAT(SUBSTR(messy_date, 1, 4),
                            '.',
                            SUBSTR(messy_date, 6, 2),
                            '.',
                            SUBSTR(messy_date, 9, 2)))) days_between
    FROM
        messy_dates;
  
